function changeStyle() {
var tabs = document.getElementsByClassName("var_radio_label");
for(var i = 0; i < tabs.length ; i++) {
var radio = document.getElementById(tabs[i].getAttribute("for"))
if(radio.checked== true){
tabs[i].getElementsByTagName("span")[0].setAttribute("class", "var_chooser selected" )
} else {
tabs[i].getElementsByTagName("span")[0].setAttribute("class", "var_chooser")
}
}
}
