template.t:
// Define the renderRentalValueSection function
function renderRentalValueSection() {
    if (clang == 'en_CA' || clang == 'en_US') {
        return `


                Total Rental Value:  ${formatRentalValue}




                Please upload your proof of insurance in the comments section below or send a copy by email.



        `;
    } else {
        return `


                Valeur locative totale:  ${formatRentalValue}




                Merci de fournir votre attestation d'assurance :



        `;
    }
}
